package com.example.online_bank.security.jwt.service;import org.springframework.security.core.Authentication;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class AuthenticationService {    public String getName(Map<String, Object> details) {        return (String) details.get("name");    }    public Map<String, Object> getDetails(Authentication authentication) {        if (authentication.getDetails() instanceof Map<?, ?>) {            return (Map<String, Object>) authentication.getDetails();        }        throw new IllegalArgumentException("Authentication details not supported");    }}