package com.example.online_bank.security.jwt.service;import io.jsonwebtoken.Claims;import org.springframework.security.core.Authentication;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic interface JwtService {    String createId();    Claims getPayload(String token);    List<String> getAuthorities(Authentication token);    List<String> getUserAuthoritiesFromClaims(String token);    String getUsername(String token);}