package com.example.online_bank.security.token;import org.springframework.security.authentication.AbstractAuthenticationToken;import org.springframework.security.core.GrantedAuthority;import java.util.Collection;public class EmailAuthenticationToken extends AbstractAuthenticationToken {    private String email;    private String code;    /**     * Конструктор для пользователя, который еще не был аутентифицирован     */    public EmailAuthenticationToken(String email, String code) {        super(null);        setAuthenticated(false);        this.email = email;        this.code = code;    }    /**     * Этот конструктор может использоваться только, когда пользователь был аутентифицирован     */    public EmailAuthenticationToken(String email, Collection<? extends GrantedAuthority> authorities) {        super(authorities);        setAuthenticated(true);        this.email = email;        this.code = null;    }    /**     * @return     */    @Override    public Object getCredentials() {        return this.code;    }    /**     * @return     */    @Override    public Object getPrincipal() {        return this.email;    }}