package com.example.online_bank.security.token;import org.springframework.security.authentication.AbstractAuthenticationToken;import org.springframework.security.core.GrantedAuthority;import java.util.Collection;import java.util.Map;public class JwtRequestToken extends AbstractAuthenticationToken {    private final String token;    /**     * Конструктор для пользователя, который еще не был аутентифицирован     */    public JwtRequestToken(String token) {        super(null);        setAuthenticated(false);        this.token = token;    }    /**     * Этот конструктор может использоваться только, когда пользователь был аутентифицирован     */    public JwtRequestToken(Collection<? extends GrantedAuthority> authorities, String token, Map<String, Object> details) {        super(authorities);        setAuthenticated(true);        this.token = token;    }    /**     * @return     */    @Override    public Collection<GrantedAuthority> getAuthorities() {        return super.getAuthorities();    }    /**     * @return     */    @Override    public Object getCredentials() {        return "";    }    /**     * @return     */    @Override    public Object getPrincipal() {        return this.token;    }    /**     * @return     */    @Override    public boolean isAuthenticated() {        return super.isAuthenticated();    }}